.input-container{
    width: 200px;
    margin: auto;
}
.input-account{
    margin-bottom: 20px;
}
.remote1{
        width: 100%;
        height: 100%;
        display: flex;
        justify-content: flex-start;
    }
    .shade{
        position: fixed;
        width:100vw;
        height: 100vh;
        left: 0;
        top:0;
        z-index: 100;
        background-color: rgba(0,0,0,0.9);
        .input-container{
            position: absolute;
            left:50%;
            top:30%;
            transform: translate(-50%, 50%);
            display: flex;
            justify-content: space-between;
            align-items: center;
            input{
                margin: 0;
            }
        }
    }
    .userList{
        border: 1px solid #ddd;
        margin-right: 50px;
        h5{
            text-align: left;
            margin-bottom: 5px;
        }
        p{
            border-bottom: 1px solid #ddd;
            line-height: 32px;
            width:200px;
            position: relative;
            overflow: hidden;
            cursor: pointer;
            span{
                position: absolute;
                left:0;
                top:100%;
                background-color: #1fbeca;
                color: #fff;
                height: 100%;
                transition: top 0.2s;
                display: block;
                width: 100%;
            }
            i{
                font-style: normal;
                font-size: 11px;
                border: 1px solid #1fbeca;
                color: #27cac7;
                border-radius: 2px;
                line-height: 1;
                display: block;
                position: absolute;
                padding: 1px 2px;
                right: 5px;
                top: 5px;
            }
        }
        p:last-child{
            border-bottom: none;
        }
        p:hover span{
            top:0;
        }
    }
    .video-container{
        display: flex;
        justify-content: center;
        >div:first-child{
            display: flex;
            justify-content: flex-start;
            margin-right: 50px;
            canvas{
                border: 1px solid #000;
            }
            ul{
                text-align: left;
            }
        }
        >div:last-child{
            .chat{
                width:500px;
                height: 260px;
                border: 1px solid #000;
                text-align: left;
                padding: 5px;
                box-sizing: border-box;
                .mes{
                    font-size: 14px;
                }
            }
            textarea{
                width:500px;
                height: 60px;
                resize: none;
            }
        }
    }
